"use strict";var $btn_person=document.querySelector(".btn-person");var $btn_planet=document.querySelector(".btn-planet");var $btn_vehicles=document.querySelector(".btn-vehicles");var $btns_box=document.querySelector(".place-for-select");var $box_list=document.querySelector(".back-button");var $list=document.querySelector(".list-group");var $btn_next=document.querySelector(".btn--nxt");var $btn_previous=document.querySelector(".btn--prvsl");var $modalBody=document.querySelector(".modal-body");var $background_page=document.querySelector(".bg-page");var $spinner=document.querySelector(".spinner");var $spinner_parent=document.querySelector(".spinner-parent");$btn_person.addEventListener("click",showPersons);$btn_planet.addEventListener("click",showPlanets);$btn_vehicles.addEventListener("click",showVehicles);var API="https://swapi.dev/api/";var data=[];var url=null;var nextUrl=null;var previousUrl=null;function showPersons(){url=API+"/people";hideBtnsBox();getNextUrl(url);$list.setAttribute("data","person")}function showPlanets(){url=API+"/planets";hideBtnsBox();getNextUrl(url);$list.setAttribute("data","planet")}function showVehicles(){url=API+"/vehicles";hideBtnsBox();getNextUrl(url);$list.setAttribute("data","vehicles")}$btn_next.addEventListener("click",function(){getNextUrl(nextUrl)});function getNextUrl(url){if(url!==null){$btn_previous.removeAttribute("disabled","disabled");axios.get(url).then(function(respond){if(data.length<=0&&respond.status===200){nextUrl=respond.data.next;data=respond.data.results;outputList(data)}else if(data.length>=1&&respond.status===200){$list.innerHTML="";nextUrl=respond.data.next;previousUrl=respond.data.previous;data=respond.data.results;outputList(data)};}).catch(function(errorMes){$background_page.innerHTML="";$background_page.style.background="white";$background_page.innerHTML="\n                    <span class=\"err\">Error: "+errorMes.message+"!</span>\n                "})}else{$btn_next.setAttribute("disabled","disabled")}}$btn_previous.addEventListener("click",getPreviousUrl);function getPreviousUrl(){if(previousUrl!==null){$btn_next.removeAttribute("disabled","disabled");axios.get(previousUrl).then(function(respond){if(data.length>=1&&respond.status===200){$list.innerHTML="";previousUrl=respond.data.previous;nextUrl=respond.data.next;data=respond.data.results;outputList(data)};}).catch(function(errorMes){$background_page.innerHTML="";$background_page.style.background="white";$background_page.innerHTML="\n                    <span class=\"err\">Error: "+errorMes.message+"!</span>\n                "})}else{$btn_previous.setAttribute("disabled","disabled")}}function hideBtnsBox(){$btns_box.classList.add("d-n")}document.querySelector(".btn-back").addEventListener("click",function(){$list.innerHTML="";$box_list.classList.add("d-n");$btns_box.classList.remove("d-n")});function outputInfo(info){$list.addEventListener("click",function(e){getInfo(e,info)})}function getInfo(e,info){var kind=e.target.parentElement.attributes[1].value;if(kind==="person"&&e.target.innerText===info.name){$modalBody.innerHTML="\n        <p>Name: "+info.name+"</p>\n        <p>Date of birth: "+info.birth_year+"</p>\n        <p>Gender: "+info.gender+"</p>\n        <p>Mass: "+info.mass+"</p>\n        <p>Height: "+info.height+"</p>\n        <p>Skin color: "+info.skin_color+"</p>\n        "}else if(kind==="planet"&&e.target.innerText===info.name){$modalBody.innerHTML="\n        <p>Name: "+info.name+"</p>\n        <p>Climate: "+info.climate+"</p>\n        <p>Diameter: "+info.diameter+"</p>\n        <p>Population: "+info.population+"</p>\n        <p>Gravity: "+info.gravity+"</p>\n        <p>Orbital period: "+info.orbital_period+"</p>\n        "}else if(kind==="vehicles"&&e.target.innerText===info.name){$modalBody.innerHTML="\n        <p>Nsme: "+info.name+"</p>\n        <p>Model: "+info.model+"</p>\n        <p>Length: "+info.length+"</p>\n        <p>Crew: "+info.crew+"</p>\n        <p>Cargo capacity: "+info.cargo_capacity+"</p>\n        <p>Cost: "+info.cost_in_credits+"</p>\n        "}}function outputList(names){names.forEach(function(item){$box_list.classList.remove("d-n");var $itemInList=document.createElement("button");$itemInList.setAttribute("type","button");$itemInList.setAttribute("data-toggle","modal");$itemInList.setAttribute("data-target","#exampleModal");$itemInList.classList.add("list-group-item","list-group-item-action","list-group-item-danger");$itemInList.textContent=item.name;$list.appendChild($itemInList);outputInfo(item)})}
